function [S1] = afficherMagnitudeFourier( nomImage )% ce script affiche la magnitude de la transform�e de Fourier de l'image% sp�cifi�e%% exemple:% afficherMagnitudeFourier('ourspolaires.jpg');A = imread(nomImage);% Calcul de la transform�e de fourier de l'imageF = fft2( double(A) );% Centrage du spectreFc = fftshift(F);% Calcul de la magnitude pour l'affichage du spectreS = log(1+abs(Fc));% Affichage de la magnitude du spectreS1 = uint8(S*255/max(S(:)));imshow(S1,[]);title(['Magnitude de la transform�e de Fourier de ', nomImage]);end